import React, { useState, useRef } from "react";

import Card from "../UI/Card";
import Button from "../UI/Button";
import ErrorModal from "../UI/ErrorModal";
import classes from "./AddUser.module.css";

const AddUser = (props) => {
  const nameInputRef = useRef(); // Holding the default name input stored as value when clicking the form button.
  const ageInputRef = useRef();

  const [error, setError] = useState();

  const addUserHandler = (event) => {
    event.preventDefault();
    console.log(nameInputRef.current.value);
    const enteredName = nameInputRef.current.value;
    const enteredUserAge = ageInputRef.current.value;

    if (enteredName.trim().length === 0 || enteredUserAge.trim().length === 0) {
      setError({
        title: "Invalid input",
        message: "Please enter a valid name and age (non-empty values).",
      });
      return;
    } //Check if something is entered inside the inputs before continuing.

    if (+enteredUserAge < 1) {
      setError({
        title: "Invalid age.",
        message: "Please enter a valid age (> 0).",
      });
      return;
    }

    console.log(enteredName, enteredUserAge);
    props.onAddUser(enteredName, enteredUserAge); //Calling the AddUserHandler" function from App.js
    // And passing these stored values from the addUserHandler function here.
  };

  const errorHandler = () => {
    setError(null);
  };

  return (
    <React.Fragment>
      {
        //If there is error display this:
        error && (
          <ErrorModal
            title={error.title}
            message={error.message}
            onConfirm={errorHandler}
          />
        )
      }
      <Card className={classes.input}>
        <form onSubmit={addUserHandler}>
          <label htmlFor="username">Username</label>
          <input id="username" type="text" ref={nameInputRef} />
          <label htmlFor="age">Age (Years)</label>
          <input id="age" type="number" ref={ageInputRef} />
          <Button type="submit">Add User</Button>
        </form>
      </Card>
    </React.Fragment>
  );
};

export default AddUser;
